import { UseResult } from './model'
import { AxiosInstance, AxiosStatic } from './axios/axios'

export interface TemplateConfigOptions {
  baseURL?: string
  axios?: AxiosStatic | any
  showErrorMessage?: (res: UseResult) => void
  showSuccessMessage?: (res: UseResult) => void
  startLoading?: () => void
  endLoading?: () => void
  handleHttpResult?: (res: any) => UseResult
  handleHttpErrorResult?: (resError: any) => UseResult
  handlePage?: (current: number, pageSize: number) => any
  handleHttpError?: <T>(resError: T) => void
}

export class Art {
  static axios?: AxiosInstance

  // Default global configuration
  static config: TemplateConfigOptions = {
    showErrorMessage: (res) => {
      console.log(res)
    },
    showSuccessMessage: (res) => {
      console.log(res)
    },
    handlePage: (page, pageSize) => {
      return { page, pageSize }
    },
    handleHttpResult: (resBody: any): UseResult<any> => {
      const { success, errorCode, errorMessage, payload, count } = resBody || {}
      return {
        success,
        code: errorCode,
        message: errorMessage,
        data: payload,
        total: count
      }
    }
  }

  /**
   * Set global configuration
   * @param config
   */
  static setup(config?: TemplateConfigOptions): void {
    if (config) {
      this.config = { ...this.config, ...config }
      if (this.config.axios) {
        this.axios = this.config.axios.create({ baseURL: this.config.baseURL })
      }
    }
  }
}
export type Method =
  | 'get'
  | 'GET'
  | 'delete'
  | 'DELETE'
  | 'head'
  | 'HEAD'
  | 'options'
  | 'OPTIONS'
  | 'post'
  | 'POST'
  | 'put'
  | 'PUT'
  | 'patch'
  | 'PATCH'
  | 'purge'
  | 'PURGE'
  | 'link'
  | 'LINK'
  | 'unlink'
  | 'UNLINK'

/**
 * API request status
 */
export enum ViewState {
  idle,
  busy,
  error
}

/**
 * API return value object
 */
export interface UseResult<T = unknown> {
  success?: boolean
  data?: T
  message?: string
  code?: string
  status?: string
  total?: number
  isCancel?: boolean
  other?: any
}

export type GetDefaultBody<T> = () => T
export type DefaultBodyType<T> = GetDefaultBody<T> | Partial<T> | T

export type RequestType<T = unknown> = (body?: Partial<T>) => Promise<any>
export type BaseConfig<R = unknown, P = unknown> = {
  status?: boolean
  loading?: boolean
  isDefaultSet?: boolean
  startLoading?: () => void
  endLoading?: () => void
  defaultBody?: DefaultBodyType<P> // 默认请求体
  method?: Method // 方法
  postBody?: (body: QueryBody<P>) => any // 转换body
  autoClear?: boolean // 自动清空
  showMessage?: boolean // 是否显示成功失败消息
  showErrorMessage?: boolean // 是否显示错误消息
  showSuccessMessage?: boolean // 是否显示成功消息
  onSuccess?: (res: UseResult<R>, cache: boolean) => void
  onError?: (res: UseResult<R>) => void
  onComplete?: (res: UseResult<R>) => void
  convertRes?: (res: any) => UseResult<R>
  postData?: (data: any) => R
  loadingDelayMs?: number
  debounceMs?: number
  throttleMs?: number
  cache?: boolean | string | ((body?: P) => string[])
  cacheKey?: boolean
  cacheTime?: number // 设置缓存数据回收时间 默认缓存数据 5 分钟后回收
  staleTime?: number // 缓存数据保持新鲜时间
}

export type ErrorType = {
  message?: string
  code?: string
  status?: string
  info?: any
}

export type StorePageConfig = {
  usePage?: boolean
  pageSize?: number
}

export type SubmitConfig<R = unknown, P = unknown> = BaseConfig<R, P> &
  QueryRunConfig

export type QueryConfig<R = unknown, P = unknown> = BaseConfig<R, P> &
  QueryRunConfig &
  StorePageConfig

export type RefreshConfigType = { status?: boolean; loading?: boolean }

export type QueryRunConfig = RefreshConfigType & {
  refresh?: boolean
}

export type HooksConfig<R = unknown, P = unknown> = SubmitConfig<R, P> & {
  manual?: boolean // 是否手动触发
  pollingIntervalMs?: number
  refreshOnWindowFocus?: boolean
  refreshOnWindowFocusMode?: 'run' | 'refresh'
  refreshOnWindowFocusTimespanMs?: number // 重新请求间隔，单位为毫秒
}

export type QueryHooksConfig<R, P> = QueryConfig<R, P> & HooksConfig<R, P>

export type BaseQueryStoreType = {
  status: ViewState
  isBusy: boolean
  isError: boolean
  isEmpty?: boolean
  setStatus: (status: ViewState) => void
  lastRequestTime?: number
  error?: ErrorType // 错误状态
}

export type QueryBodyStoreType<P> = {
  body?: Partial<P>
  setBody: (body: Partial<P>, replace?: boolean) => void
}

export type QueryDataStoreType<R = unknown> = {
  originData?: any
  data?: R
  setData: (data?: R) => void
}

export type QueryPageStoreType<R = unknown> = {
  current: number
  pageSize: number
  total: number
  offset?: string
  setPage: (config: {
    current?: number
    pageSize?: number
  }) => Promise<UseResult<R>>
  loadMore?: () => Promise<UseResult<R>>
}

export type QueryRunStoreType<R, P> = {
  cancel: (message?: string) => void
  run: (body?: Partial<P>, config?: QueryRunConfig) => Promise<UseResult<R>>
  refresh: (config?: RefreshConfigType) => Promise<UseResult<R>>
  clear: () => void
}

export type StoreType<R = unknown, P = unknown> =
  | QueryStoreType<R, P>
  | SubmitStoreType<R, P>

export type QueryStoreType<R = unknown, P = unknown> = BaseQueryStoreType &
  QueryBodyStoreType<P> &
  QueryDataStoreType<R> &
  QueryPageStoreType<R> &
  QueryRunStoreType<R, P>

export type SubmitStoreType<R, P> = BaseQueryStoreType &
  QueryBodyStoreType<P> &
  QueryDataStoreType<R> &
  QueryRunStoreType<R, P>

export type PaginationType = {
  current: number
  pageSize: number
  total: number
  offset?: string
}

export type CachedData<TData = unknown, TBody = unknown> = {
  data: TData
  body: TBody
  pagination?: PaginationType
  time: number
}

export type RequestResult = {
  type: 'axios'
  request: () => Promise<any>
  source?: any
}

export type QueryBody<P> = Partial<P> & { current?: number; pageSize?: number }
import { CachedData } from './model'

const dataCache: Map<string, CachedData> = new Map()

export function createCacheKey(key?: string, ids?: string[]) {
  const prefix = 'key'
  return `${prefix}_${key}` + ids?.map((id) => `_${id}`)
}

export function setCache<TData, TBody>(
  key: string,
  data: CachedData<TData, TBody>
) {
  dataCache.set(key, data)
}

export function getCache<TData, TBody>(
  key: string
): CachedData<TData, TBody> | undefined {
  return dataCache.get(key) as CachedData<TData, TBody> | undefined
}

export function clearCache(key: string) {
  dataCache.delete(key)
}
import { Art } from '../art'
import { AxiosError, AxiosRequestConfig, Method } from './axios'

export function getAxiosRequest(
  method: Method,
  url: string,
  body: any,
  config?: AxiosRequestConfig
): Promise<any> {
  switch (method) {
    case 'POST':
    case 'post':
      return Art.axios!.post(url, body, config)
    case 'PUT':
    case 'put':
      return Art.axios!.put(url, body, config)
    case 'DELETE':
    case 'delete':
      return Art.axios!.delete(url, config)
    default:
      return Art.axios!.get(url, config)
  }
}

export function handleError(e: any) {
  if (Art.config.axios.isCancel(e)) {
    return { success: false, isCancel: true, message: e.message }
  } else {
    const response = e.response
    if (response) {
      const axiosRes = e as AxiosError
      if (Art.config.handleHttpErrorResult) {
        return {
          message: axiosRes.message,
          ...Art.config.handleHttpErrorResult(response),
          success: false,
          status: response.status,
          isCancel: false
        }
      } else {
        return {
          success: false,
          status: response.status,
          code: axiosRes.code,
          message: axiosRes.message,
          isCancel: false
        }
      }
    } else {
      throw new Error(e)
    }
  }
}
import {
  CachedData,
  DefaultBodyType,
  GetDefaultBody,
  Method,
  PaginationType,
  QueryBody,
  QueryConfig,
  QueryRunConfig,
  QueryStoreType,
  RefreshConfigType,
  RequestResult,
  RequestType,
  StoreType,
  SubmitStoreType,
  UseResult,
  ViewState
} from './model'
import { Art } from './art'
import { clearCache, createCacheKey, getCache, setCache } from './cache'
import { getAxiosRequest, handleError } from './axios'

/**
 * 默认状态
 */
export function getDefaultState() {
  return {
    isBusy: false,
    isError: false,
    status: ViewState.idle,
    isEmpty: undefined,
    error: undefined,
    lastRequestTime: undefined,
    body: undefined,
    originData: undefined,
    data: undefined
  }
}

/**
 * 处理默认请求体
 * @param store 状态管理
 * @param defaultBody 默认请求内容
 * @param body 动态请求参数
 */
export function updateDefaultBody<P>(
  store: StoreType<any, P>,
  defaultBody?: DefaultBodyType<P>,
  body?: Partial<P>
) {
  let _body
  if (defaultBody) {
    let _defaultBody
    if (typeof defaultBody === 'function') {
      _defaultBody = (defaultBody as GetDefaultBody<P>)()
    } else {
      _defaultBody = defaultBody
    }
    _body = { ..._defaultBody, ...(body ?? {}) }
  } else {
    _body = body
  }
  store.body = _body
}

/**
 * 添加分页请求参数
 * @param store
 * @param usePage 是否使用分页
 */
export function handlePageBody<R, P>(
  store: StoreType<R, P>,
  usePage?: boolean
): any {
  let _body = store.body as any
  if (usePage) {
    const _store = store as QueryStoreType<R, P>
    if (Art.config.handlePage) {
      _body = {
        ...(_body ?? {}),
        ...Art.config.handlePage(_store.current, _store.pageSize)
      }
    } else {
      _body = {
        ...(_body ?? {}),
        current: _store.current,
        pageSize: _store.pageSize
      }
    }
  }
  return _body
}

/**
 * 转化成提交之前的请求体
 * @param body
 * @param postBody
 */
export function getPostBody<P>(
  body?: QueryBody<P>,
  postBody?: (body: QueryBody<P>) => any
) {
  let _body = body
  if (postBody && _body) {
    _body = postBody(_body)
  }
  return _body
}

/**
 * 自动清除
 * @param store
 * @param autoClear
 */
export function autoClear(store: { clear: () => void }, autoClear?: boolean) {
  if (autoClear) {
    store.clear()
  }
}

/**
 * 获取当前配置项目
 * @param config
 */
export function getMyConfig<R, P>(
  config?: QueryConfig<R, P>
): QueryConfig<R, P> {
  // 初始化默认配置
  const defaultConfig = {
    status: true,
    loading: false,
    isDefaultSet: true,
    autoClear: false,
    cacheTime: 300000,
    staleTime: 0,
    showMessage: true,
    showSuccessMessage: false,
    showErrorMessage: true
  } as QueryConfig<R, P>
  // 得到当前配置
  // @ts-ignore
  return { ...defaultConfig, ...(config ?? {}) }
}

/**
 * 处理开始loading
 * @param config
 */
export function handleStartLoading(config: {
  loading?: boolean
  startLoading?: () => void
}) {
  if (config.loading && config.startLoading) {
    config.startLoading()
  }
}

/**
 * 处理结束loading
 * @param config
 */
export function handleEndLoading(config: {
  loading?: boolean
  startLoading?: () => void
}) {
  if (config.loading && config.startLoading) {
    config.startLoading()
  }
}

export async function doRequest<T, P>(
  request: RequestResult,
  store: SubmitStoreType<T, P>,
  config: QueryConfig<T, P>,
  setData: (res: UseResult<T>) => void
): Promise<UseResult<T>> {
  // 处理开始loading
  handleStartLoading(config)

  const setStatus = async (status: ViewState) => {
    const loadingWait = async () => {
      if (status !== ViewState.busy && config.loadingDelayMs) {
        await waitTime(config.loadingDelayMs)
      }
    }

    if (config?.status) {
      await loadingWait()
      store.setStatus(status)
    }
  }

  // 发送请求
  let myRes: UseResult<T>
  try {
    // 设置状态
    await setStatus(ViewState.busy)
    // 请求接口
    const res = await request.request()
    // 转换数据
    myRes = config.convertRes
      ? config.convertRes(res)
      : Art.config.handleHttpResult
      ? Art.config.handleHttpResult(res)
      : res
    // 设置原始值
    store.originData = myRes.data
    // 转换成前端想要的数据格式
    if (config.postData) {
      myRes.data = config.postData(myRes.data)
    }
    // 设置状态
    await setStatus(ViewState.idle)
  } catch (e) {
    // 处理异常
    myRes = handleRequestCatch(e, request) as UseResult<T>
    if (!myRes.isCancel) {
      // 设置状态
      await setStatus(ViewState.error)
    } else {
      // 设置状态
      await setStatus(ViewState.idle)
    }
  }

  setData(myRes)

  // 处理回调
  handleCallback<T, P>(config, myRes)

  // 处理消息
  handleMessage<T, P>(config, myRes)

  // 结束loading
  handleEndLoading(config)
  return myRes
}

// 处理消息
function handleMessage<T, P>(config: QueryConfig<T, P>, res: UseResult) {
  if (config.showMessage) {
    if (
      res.success &&
      config.showSuccessMessage &&
      Art.config.showSuccessMessage
    ) {
      Art.config.showSuccessMessage(res)
    } else if (
      !res.success &&
      config.showErrorMessage &&
      Art.config.showErrorMessage
    ) {
      Art.config.showErrorMessage(res)
    }
  }
}

// 处理回调
function handleCallback<T, P>(config: QueryConfig<T, P>, res: UseResult<T>) {
  // 请求结束
  if (res.success) {
    if (config.onSuccess) {
      config.onSuccess(res, false)
    }
  } else if (!res.isCancel) {
    if (config.onError) {
      config.onError(res)
    }
  }
  if (config.onComplete) {
    config.onComplete(res)
  }
}

/**
 * 处理请求失败
 * @param e
 * @param request
 */
function handleRequestCatch(e: any, request: RequestResult): UseResult {
  let result = { success: false, isCancel: false, message: e } as UseResult
  if (request.type === 'axios') {
    result = handleError(e)
  }

  if (Art.config.handleHttpError) {
    Art.config.handleHttpError(e)
  }
  console.log(e)
  return result
}

/**
 * 创建请求
 * @param request 请求或者地址
 * @param body 请求阐述
 * @param method 方法
 */
export function getRequest(
  request: RequestType | string,
  body?: any,
  method?: Method
): RequestResult {
  let _request: () => Promise<any>

  let source: any

  if (typeof request === 'function') {
    _request = () => request(body)
  } else {
    let url = request as string
    const isPathParams = url.includes('{') && url.includes('}')
    const _method = method ?? (isPathParams ? 'GET' : body ? 'POST' : 'GET')
    const isPost = method === 'POST' || method === 'post'
    if (body && typeof body === 'object' && isPathParams) {
      Object.keys(body).forEach((key) => {
        // @ts-ignore
        url = url.replace(new RegExp('\\{' + key + '\\}', 'g'), body[key])
      })
    }

    if (Art.config.axios != null) {
      source = Art.config.axios.CancelToken.source()
      _request = () =>
        getAxiosRequest(_method, url, body ?? (isPost ? {} : undefined), {
          cancelToken: source.token
        })
    } else {
      throw new Error(
        'Art must pass in the http object, currently only supports axios'
      )
    }
  }
  return { request: _request, type: 'axios', source }
}

export function setBody<P>(
  store: { body?: Partial<P> },
  inBody: Partial<P>,
  replace = false
): void {
  const { body } = store
  if (body && !replace) {
    store.body = { ...inBody, ...store.body }
  } else {
    store.body = inBody
  }
}

/**
 * 延迟等待
 * @param time 等待时间
 */
export const waitTime = (time = 100) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(true)
    }, time)
  })
}

/**
 * 处理请求
 * @param request
 * @param config
 */
export function doRun<R, P>(
  request: (
    body?: Partial<P>,
    config?: QueryRunConfig
  ) => Promise<UseResult<R>>,
  config: QueryConfig<R, P>
) {
  if (config.throttleMs) {
    return throttle(request, config.throttleMs)
  }
  return debounce(request, config.debounceMs)
}

// 防抖函数
export function debounce<R, P>(
  request: (
    body?: Partial<P>,
    config?: QueryRunConfig
  ) => Promise<UseResult<R>>,
  ms?: number
) {
  let timeout: any
  return (
    body?: Partial<P>,
    config?: QueryRunConfig
  ): Promise<UseResult<R>> => {
    clearTimeout(timeout)
    return new Promise((resolve) => {
      if (ms) {
        timeout = setTimeout(() => {
          resolve(request(body, config))
        }, ms)
      } else {
        resolve(request(body, config))
      }
    })
  }
}

// 节流函数
export function throttle<R, P>(
  request: (
    body?: Partial<P>,
    config?: QueryRunConfig
  ) => Promise<UseResult<R>>,
  waitMs: number
) {
  let timeout: any
  let old = 0
  return (
    body?: Partial<P>,
    config?: QueryRunConfig
  ): Promise<UseResult<R>> => {
    return new Promise((resolve) => {
      const now = new Date().valueOf()
      if (!old) {
        old = now
      }
      if (now - old > waitMs) {
        if (timeout) {
          clearTimeout(timeout)
          timeout = null
        }
        resolve(request(body, config))
        old = now
      } else if (!timeout) {
        timeout = setTimeout(() => {
          old = new Date().valueOf()
          timeout = null
          resolve(request(body, config))
        }, waitMs)
      }
    })
  }
}

/**
 * 处理数据缓存
 * @param config
 * @param request
 * @param store
 * @param res
 */
export function setStoreCacheData<R, P>(
  config: QueryConfig<R, P>,
  request: RequestType<P> | string,
  store: StoreType<R, P>,
  res: UseResult<R>
) {
  if (!config.cache) {
    return
  }

  const key = getCacheKey(config, request, store)

  let pagination: PaginationType | undefined
  try {
    if (config.usePage) {
      const { current, pageSize, total, offset } = store as QueryStoreType<R, P>
      pagination = { current, pageSize, total, offset }
    }
  } catch (e) {}

  setCache<UseResult<R>, P>(key, {
    body: store.body as P,
    data: res,
    time: new Date().getTime(),
    pagination
  })
