      _store.pageSize = pageSize
      _store.total = total
      _store.offset = offset
    } catch (e) {}
  }
  store.data = cache.data.data
  store.body = cache.body

  // 控制新鲜度, 如果过期新鲜度
  const staleTime = config.staleTime ?? 0
  if (staleTime >= 0 && new Date().getTime() - cache.time > staleTime) {
    store
      .run(undefined, { loading: false, status: false, refresh: true })
      .then()
  }
  return new Promise((resolve) => resolve(res))
}

export function getCacheKey<R, P>(
  config: QueryConfig<R, P>,
  request: RequestType<P> | string,
  store: StoreType<R, P>
) {
  let key

  if (config.cache === true || typeof config.cache === 'function') {
    if (typeof request === 'function') {
      throw new Error(
        'When request is a function, the configuration item cache can only be a string'
      )
    }
  }

  if (typeof config.cache === 'string') {
    key = createCacheKey(config.cache)
  } else if (typeof config.cache === 'function') {
    const ids = config.cache(store.body as P)
    key = createCacheKey(request as string, ids)
  } else {
    key = createCacheKey(request as string)
  }
  if (config.usePage) {
    const { current, pageSize } = store as QueryStoreType<R, P>
    return `${key}_${current}_${pageSize}`
  }
  return key
}

export function setStatus(store: StoreType, status: ViewState) {
  store.status = status
  store.isError = status === ViewState.error
  store.isBusy = status === ViewState.busy
}

export function setPage<R>(
  config: {
    current?: number
    pageSize?: number
  },
  store: QueryStoreType<R>
): Promise<UseResult<R>> {
  const { current, pageSize } = config ?? {}
  if (current) {
    store.current = current
  }
  if (pageSize) {
    store.pageSize = pageSize
  }
  return store.run()
}

export function refresh<R, P>(
  myConfig: QueryConfig<R, P>,
  store: StoreType<R>,
  request: RequestType<P> | string,
  currentRequest: RequestResult | undefined,
  config?: RefreshConfigType
) {
  myConfig = {
    ...myConfig,
    loading: false,
    status: false,
    ...(config ?? {}),
    refresh: true
  }
  if (!currentRequest) {
    return store.run(undefined, myConfig)
  } else {
    return doRequest<R, P>(currentRequest, store, myConfig, (res) =>
      setResData(res, myConfig, store, request)
    )
  }
}

export function cancel(
  currentRequest: RequestResult | undefined,
  message?: string
) {
  if (currentRequest) {
    if (currentRequest.type === 'axios') {
      // eslint-disable-next-line no-unused-expressions
      currentRequest.source?.cancel(message)
    }
  }
}

export function getRequestFun<R, P>(
  myConfig: QueryConfig<R, P>,
  store: StoreType<R>,
  request: RequestType<P> | string,
  currentRequest: RequestResult | undefined,
  body?: Partial<P>,
  config?: QueryRunConfig
): Promise<UseResult<R>> {
  myConfig = { ...myConfig, ...config }
  // 清除
  autoClear(store, myConfig.autoClear)

  // 获取缓存
  const { cache, active } = getStoreCacheData<R, P>(myConfig, request, store)

  // 如果有缓存 并且缓存有效
  if (!config?.refresh && cache && active) {
    return getCacheRequest<R, P>(myConfig, cache, store)
  } else {
    // 设置body
    updateDefaultBody<P>(store, myConfig.defaultBody, body)
    // 处理分页
    let _body = handlePageBody(store, myConfig.usePage)
    // 获取准备提交的请求体
    _body = getPostBody(_body, myConfig.postBody)
    // 获取请求体
    currentRequest = getRequest(request, _body, myConfig.method)
    // 发送请求
    return doRequest<R, P>(currentRequest, store, myConfig, (res) =>
      setResData(res, myConfig, store, request)
    )
  }
}

// 设置返回数据
function setResData<R, P>(
  res: UseResult<R>,
  myConfig: QueryConfig<R, P>,
  store: StoreType<R>,
  request: RequestType<P> | string
) {
  if (res.success) {
    if (myConfig.isDefaultSet) {
      store.setData(res.data)
      if (myConfig.usePage) {
        ;(store as QueryStoreType).total = res.total ?? 0
      }
    }
    if (myConfig?.status && store?.status !== ViewState.error) {
      store.isEmpty =
        !res.data || (res.data && res.data instanceof Array && !res.data.length)
    }
    // 记录最后时间
    store.lastRequestTime = new Date().getTime()

    // 处理缓存
    setStoreCacheData(myConfig, request, store, res)
  } else if (!res.isCancel) {
    store.error = { message: res.message, code: res.code, status: res.status }
  }
}
import React, { useEffect } from 'react'
// @ts-ignore
const document: any = window ? window.document : undefined
const visible = 'visible'

/**
 * 检查浏览器标签切换
 */
export const useBrowserPageChange = () => {
  const [visibilityChange, setVisibilityChange] = React.useState<boolean>(false)

  const getHiddenProp = () => {
    if (!document) {
      return null
    }
    const prefixes = ['webkit', 'moz', 'ms', 'o']

    // if 'hidden' is natively supported just return it
    if ('hidden' in document) {
      return 'hidden'
    }

    // otherwise loop over all the known prefixes until we find one
    for (let i = 0; i < prefixes.length; i++) {
      if (prefixes[i] + 'Hidden' in document) return prefixes[i] + 'Hidden'
    }

    // otherwise it's not supported
    return null
  }

  const getVisibilityState = () => {
    const prefixes = ['webkit', 'moz', 'ms', 'o']
    if ('visibilityState' in document) return 'visibilityState'
    for (let i = 0; i < prefixes.length; i++) {
      if (prefixes[i] + 'VisibilityState' in document)
        return prefixes[i] + 'VisibilityState'
    }
    // otherwise it's not supported
    return null
  }

  useEffect(() => {
    const eventName =
      (getHiddenProp() ?? '').replace(/[H|h]idden/, '') + 'visibilitychange'
    if (getHiddenProp()) {
      document.addEventListener(
        eventName,
        function () {
          setVisibilityChange(document[getVisibilityState() ?? ''] === visible)
        },
        false
      )
    }
    return () => {
      document.removeEventListener(eventName, function () {
        setVisibilityChange(document[getVisibilityState() ?? ''] === visible)
      })
    }
  }, [getHiddenProp()])

  return { visibilityChange }
}
import { DependencyList, useEffect } from 'react'
import { HooksConfig, StoreType } from '../model'
import { useBrowserPageChange } from './browser-page-hooks'

export function useCommonHooks(
  store: StoreType,
  type: 'submit' | 'query',
  config?: HooksConfig,
  deps?: DependencyList
) {
  useEffect(() => {
    const manual = config?.manual ?? type === 'submit'
    if (!manual) {
      store.run().then()
    }
    let interval: any
    if (config?.pollingIntervalMs) {
      if (interval) {
        clearInterval(interval)
      }
      interval = setInterval(() => {
        store.run().then()
      }, config?.pollingIntervalMs)
    }
    return () => {
      if (interval) {
        clearInterval(interval)
      }
      store.cancel()
    }
  }, deps ?? [])

  const { visibilityChange } = useBrowserPageChange()

  useEffect(() => {
    if (config?.refreshOnWindowFocus && visibilityChange) {
      if (
        !store.lastRequestTime ||
        new Date().getTime() - store.lastRequestTime >
          (config?.refreshOnWindowFocusTimespanMs ?? 0)
      )
        if (config?.refreshOnWindowFocusMode === 'run') {
          store.run().then()
        } else {
          store.refresh().then()
        }
    }
  }, [visibilityChange])
}
import { DependencyList, useMemo } from 'react'
import {
  cancel,
  doRun,
  getDefaultState,
  getMyConfig,
  getRequestFun,
  refresh,
  setBody,
  setPage,
  setStatus
} from './utils'
import {
  QueryConfig,
  QueryHooksConfig,
  QueryRunConfig,
  QueryStoreType,
  RequestResult,
  RequestType,
  UseResult,
  ViewState
} from './model'
import resso from 'resso'
import { useCommonHooks } from './hooks'

export function createQueryStore<
  R = Record<string, any> | string,
  P = Record<string, any>
>(request: RequestType<P> | string, config?: QueryConfig<R, P>) {
  // 得到当前配置
  const myConfig = getMyConfig<R, P>(config) as QueryConfig<R, P>

  // 当前请求
  let currentRequest: RequestResult | undefined

  // 创建store
  const store: QueryStoreType<R, P> = resso<QueryStoreType<R, P>>({
    ...getDefaultState(),
    current: 1,
    pageSize: config?.pageSize ?? 10,
    total: 0,
    offset: undefined,
    setStatus: (status: ViewState) => {
      setStatus(store, status)
    },
    setBody: (inBody: Partial<P>, replace = false) => {
      setBody<P>(store, inBody, replace)
    },
    setPage: (config): Promise<UseResult<R>> => setPage(config, store),
    setData: (data?: R) => {
      store.data = data
    },
    refresh: (config): Promise<UseResult<R>> => {
      return refresh(myConfig, store, request, currentRequest, config)
    },
    run: doRun<R, P>(
      (body?: Partial<P>, config?: QueryRunConfig) =>
        getRequestFun(myConfig, store, request, currentRequest, body, config),
      myConfig
    ),
    cancel: (message?: string) => {
      cancel(currentRequest, message)
    },
    clear: () => {
      store.data = undefined
      store.body = undefined
      store.total = 0
      store.current = 1
      store.isEmpty = undefined
    }
  })
  return store
}

export function useQuery<
  R = Record<string, any> | string,
  P = Record<string, any>
>(
  request: RequestType<P> | string,
  config?: QueryHooksConfig<R, P>,
  deps?: DependencyList
) {
  const store = useMemo(() => createQueryStore(request, config), deps ?? [])
  useCommonHooks(store, 'query', config, deps)
  return store
}
import { DependencyList, useMemo } from 'react'
import {
